item.description:
").addClass("band-item");
                $li.append($("<p>").html("<b>" + bandShow.name + "</b> - <em>" + bandShow.date + "</em>"));
                $("#bandlist").append($li);
              });
            },
            complete: function() {
              $("#getBands").remove();
              $(".bandlist").addClass("invisible"); //Resize first
              $(".bandlist").removeClass("hidden");
              setTimeout(function() {
                $(".bandlist-loading").addClass("hidden");
                $(".bandlist").removeClass("invisible");
              }, 100);
            }
          });

          return false;
        });
      });

      window.markers[venue.id] = marker;
      window.venues[venue.id] = venue;

      var showAllVenues = $('input#showAllVenues').prop('checked');
      if (!showAllVenues) {
        if (venue.show_count &gt; 0) {
          marker.setMap(map);
        }
      } else {
        marker.setMap(map);
      }

      if (venue.show_count &gt; 0) {
        return { location: position, weight: venue.show_count / 100.0 };
      }
    }).compact().value();

    var heatmap = new google.maps.visualization.HeatmapLayer({
      data: heatMapData
      , radius: 20
      , map: map
    });
  };

  var updateVenuePopup = function(venue) {
    window.infoWindowContent[venue.id] = generateInfoContent(venue);

    if (window.currentVenue === venue) {
      var addRemove = venue.selected ? 'remove' : 'add';
      var link = $("span.venue-info-map[data-venue-id='" + venue.id + "']");
      var icon = $("span.venue-info-map[data-venue-id='" + venue.id + "'] .icon");
      var onclick = addRemove + "VenueClickHandler('" + venue.id.replace("'", "\\'") + '\',\'' + venue.name.replace("'", "\\'") + '\')';

      link.attr("onclick", onclick);
      icon.removeClass();
      icon.addClass("icon");
      icon.addClass(addRemove + "-icon");
    }
  };

  $(function() {
    $('#user_venues_genres').on('new-items', function() {
      var venues = $('#venue_map').data('heat-maps');
      var selectedVenues = $("#user_venues_genres").select2("data");
      _(venues).each(function(venue) {
        if (_(selectedVenues).find(function(selectedVenue) { return selectedVenue.id === venue.id; })) {
          if (window.currentVenue === venue) {
            venue.follower_count += 1;
          }
          venue.selected = true;
          updateVenuePopup(venue);
        } else {
          if (window.currentVenue === venue) {
            venue.follower_count -= 1;
          }
          venue.selected = false;
          updateVenuePopup(venue);
        }
      });
    });

    $("input#showAllVenues").on("change", function() {
      _(window.markers).each(function(marker, venueId) {
        if (!$("input#showAllVenues").prop("checked")) {
          if (window.venues[venueId].show_count &gt; 0) {
            marker.setMap(window.map);
          } else {
            marker.setMap(null);
          }
        } else {
          marker.setMap(window.map);
        }
      });
    });

    $("#fullmapToggle").on("click", function(event) {
      if ($("body").hasClass("fullscreen")) {
        var $map = $("#mapView");
        $map.detach();
        $("#map_holder").append($map);
        $("body").removeClass("fullscreen");

        $('html,body').scrollTop($("a[name=GetStarted]").offset().top);
        var center = window.map.getCenter()
        google.maps.event.trigger(window.map, 'resize');
        window.map.setCenter(center);
        window.map.setZoom(window.map.getZoom() - 1);

        return false;
      } else {
        var $map = $("#mapView");
        $map.detach();
        $("body").append($map);
        $("body").addClass("fullscreen");

        var center = window.map.getCenter()
        google.maps.event.trigger(window.map, 'resize');
        window.map.setCenter(center);
        window.map.setZoom(window.map.getZoom() + 1);
      }
    });
  });


</p>
